@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {

}
<div class="container">
    <nav class="nav">
        <a routerLink="/">Home</a>
        <a routerLink="user/me">Me</a>
        <ng-template [ngIf]="isLogged"></ng-template>


    </nav>
</div>
