import { Component } from '@angular/core';

@Component({
  selector: 'app-me',
  templateUrl: './me.component.html',
  styleUrls: ['./me.component.css']
})
export class MeComponent {
  user: MeResponse
  hasError =  false;
  constructor(private meSercice: MeService, private authorizedService: AuthorizedService){}
  ngOnInit(): void {
    this.authorizedService.authorized();
    this.meSercice.me()
      .subscribe((res) => {
          this.user = res;
      }, (err) => {
          this.hasError = true;
      })  

  }

}
