import { Component, OnInit } from '@angular/core';
import { MeModel } from 'src/app/model/me.model';
import { AuthService } from 'src/app/services/auth.service';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit{

  user: MeModel
  hasError =  false;

  constructor(private userService: UserService){

  }




  ngOnInit(): void {

    this.userService.me()
      .subscribe((res) => {
          this.user = res;
      }, (err) => {
          this.hasError = true;
      })  

  }

}
<div class="sidebar">
    <div class="user-welcome">
        <p>Welcome {{user.firstName}}{{u}}!</p>
    </div>
   <div class="links">
    <a class="a-link" href="user/me" >Profile</a>
    <a  class="a-link" href="user/all">Users</a>
    <a  class="a-link" href="user/reset">Reset password</a>

   </div>
   <div class="logout-div">
        <p class="p-logout" >Logout</p>
   </div>


</div>
