import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class UserService {

  constructor(private http: HttpClient, private cookieService: CookieService) { }


  me(): Observable<any>{
    return this.http.get(this.apiUrl, {headers: new HttpHeaders().set('Authorization',this.cookieService.get('token') )});
  }

}
